import React, { useState } from 'react';
import './Payment.css';

import {
    BrowserRouter as Router,
    useHistory,
    useLocation,
    Switch,
    Route,
    Link
} from "react-router-dom";
function Payment(props) {
    const location = useLocation();
    const [client, setClient] = useState(location.state.client);
    const [payment, setPayment] = useState({});

    function handleChange(evt) {
        const value = evt.target.value;
        let valInt = evt.target.value.replace(/\D/, '')
        handleChange(evt, valInt);
    }
    function handleChangeInt(evt) {
        const value = evt.target.value;
        handleChange(evt, value);
    }
    function handleChange(evt, val) {
        const paymentCopy = payment;
        paymentCopy[evt.target.name] = valInt;
        setClient({ payment: paymentCopy });
    }
    const onSubmit = async (event) => {
        event.preventDefault();


    }
    return (
        <div >
            <h2>Payment method</h2>
            <h2>{client.Name}</h2>
            <div class="formcontainer">
                <form onSubmit="{onSubmit}">
                    <label> Card no:</label>
                    <input type="text" name="CardNo" pattern="[0-9]*" value={payment.CardNo} onChange={handleChangeInt} ></input>
                    <div>
                        <label> Cv code:</label>
                        <input type="text" value={payment.CvCode} onChange={handleChangeInt}></input>
                    </div>
                    <div>Holder name:
                <input type="text" value={payment.HolderName} onChange={handleChange}></input>
                    </div>
                    <div>Validity date:
                <input type="date" value={payment.ValidityDate} onChange={handleChange}></input>
                    </div>
                    <div>
                        <input type="submit"></input>
                    </div>
                </form>
            </div>
        </div>
    );
}
export default Payment;
