import React, {useState} from 'react';
import axios from 'axios';
import logo from './logo.svg';
import './App.css';

var  test = 0;

const testData = [{
  ID:1,
  IDC:"324",
    Name: "gilad",
  },
  {
    ID:2,
    IDC:"323444",
      Name: "or",
    }]
  const TableHeader = ()=>{
    return (

  <div class="tr">
        <span class="td">ID</span>
        <span class="td">Name</span>
        <span class="td">Save</span>
  </div>
      )
  }

  const OneCient = (props)=>{
     const [client, setClient] = useState(props.Client);
     const OnSave =()=>{
      event.preventDefault();
      console.log("clicked");
    }
    return (     
      <form class="tr">    
            <span class="td">
              <input type="hidden" id="id" name="id" value={client.ID}/>
              <input  type="text" value={ client.IDC}
                  onChange={event =>{                 
                    client.IDC = event.target.value; setClient( {client });
                  }
           }
                /></span>
            <span class="td"><input  type="text" value={ client.Name}
              onChange={event =>{                 
                    client.Name = event.target.value; setClient( {client });
                  }   
                }
                               /></span>
           <span class="td"><button >Save</button></span>       
       </form>   
    )
  }
  const TableValues = (props)=>{
   return(props.clients.map(client =><OneCient Client ={client}/>));
  }
  const AddClient = (props)=>{
   return( <button 
             onClick={() => props.addClient()}
             >Add</button>)

  }
  const getData =async (myGetData)=>{
    var data;
    if (test ==1)
      data = testData;
    else {
    const resp = await axios.get(`http://localhost:58546/api/clients`);
    data = resp.data;
    }
    myGetData(data);    
  }
  const GetClients = (props)=>{
    return( <button 
              onClick={() =>getData( props.onClickClients)}
              >Get Clients</button>)

   }


  function App() {  
    const [readClients, setReadClients] = useState([]);
    const onAddClient = ()=> {setReadClients(readClients.concat({}))}
    const onGetData = (clientsArray)=>{
      console.log(clientsArray);
      setReadClients(clientsArray);
    }
    //getData(onGetData);
    return (
      <div className="App">
        <header className="App-header">
        <GetClients onClickClients={onGetData}/>
          <AddClient addClient={onAddClient}/>
          <div class="table">
            <TableHeader/>
            <TableValues clients = {readClients}/>
          </div>
        </header>
      </div>
    );
  }

export default App;
