import { useState } from "react";

const getCards = async (myGetData) => {

    let add = `http://localhost:58546/api/movies`;
    console.log(add);
    const resp = await axios.get(add);
    let data = resp.data;

    myGetData(data);
}
function Card(props) {
    return (
        <div>
            <img src={props.movie.Poster}></img>
        </div>
    )
}
function Cards() {
    [movies, setMovies] = useState([]);
    function onGetData(data) {
        setMovies(data);
    }
    useEffect(() => {
        getCards(onGetData);
    }, []); // <-- empty dependency array
    return (
        <div>
            {movies.map(movie => <Card {...movie} />)}
        </div>
    )
}
export default Cards;
