import React, { useState, useEffect } from 'react';
import './Payment.css';
import './Movies.css';
import axios from 'axios';

import {
    BrowserRouter as Router,
    useHistory,
    useLocation,
    Switch,
    Route,
    Link
} from "react-router-dom";
function ShowMovie() {


}
function SearchForm(props) {
    const [movieName, setMovieName] = useState("");
    //  const location = useLocation();
    function handleChange(evt) {
        const value = evt.target.value;
        setMovieName(value);
    }
    async function onSubmitForm(event) {
        let jsonSent = { movieName: movieName };
        console.log(jsonSent, "jsonSent ");
        event.preventDefault();
        const resp = axios.post('http://localhost:58546/api/Movies', jsonSent);
        var movie = (await resp).data;
        props.SetMovie(movie);
    }
    return (
        <div>
            <h2>Search Movies</h2>

            <form onSubmit={onSubmitForm}>
                <div>
                    <label> Movie name:</label>
                    <input type="text" name="movieName" value={movieName} onChange={handleChange} ></input>
                </div>
                <div>
                    <input type="submit"></input>
                </div>
            </form>
        </div>
    );
}
function DisplayMovie(props) {
    return (
        <div>
            <span class="movName">{props.move.Title}</span>
            <img class="poster" src={props.move.Poster}></img>
            <span class="plot">{props.move.Plot}</span>
        </div >
    );
}
function Movies(props) {
    const [movie, setMovie] = useState({});
    function handleSetMovie(mov) {
        setMovie({ ...mov })
    }
    return (
        <div className="App">

            <SearchForm SetMovie={handleSetMovie} />
            <DisplayMovie move={movie} />
        </div>
    );
}
export default Movies;
