import React, { useState } from 'react';
import './Payment.css';
import axios from 'axios';

import {
    BrowserRouter as Router,
    useHistory,
    useLocation,
    Switch,
    Route,
    Link
} from "react-router-dom";

function PaymentForm(props) {
    //  const location = useLocation();
    console.log(props.client, "form client");
    const [client, setClient] = useState(props.client);
    const [payment, setPayment] = useState({});

    function handleChangeInt(evt) {
        const value = evt.target.value;
        let valInt = evt.target.value.replace(/\D/, '')
        handleChangeG(evt, valInt);
    }
    function handleChange(evt) {
        const value = evt.target.value;
        handleChangeG(evt, value);
    }
    function handleChangeG(evt, val) {
        const paymentCopy = payment;
        paymentCopy[evt.target.name] = val;
        setClient({ payment: paymentCopy });
    }
    async function onSubmitForm(event) {
        event.preventDefault();
        const resp = axios.post('http://localhost:58546/api/payment', { payment: payment, clientId: client.ID });

    }
    return (
        <div >
            <h2>Payment method</h2>
            <h2>{client.Name}</h2>
            <div class="formcontainer">
                <form onSubmit={onSubmitForm}>
                    <label> Card no:</label>
                    <input type="text" name="CardNo" pattern="[0-9]*" value={payment.CardNo} onChange={handleChangeInt} ></input>
                    <div>
                        <label> Cv code:</label>
                        <input type="text" name="CvCode" value={payment.CvCode} onChange={handleChangeInt}></input>
                    </div>
                    <div>Holder name:
                <input type="text" name="HolderName" value={payment.HolderName} onChange={handleChange}></input>
                    </div>
                    <div>Validity date:
                <input type="date" name="ValidityDate" value={payment.ValidityDate} onChange={handleChange}></input>
                    </div>
                    <div>
                        <input type="submit"></input>
                    </div>
                </form>
            </div>
        </div>
    );
}
function GetPayment(props) {
    return (
        <button onClick={() => getPaymentData(props.onClickClients, props.clientId)}> Get payment
        </button>
    );
}

const getPaymentData = async (myGetData, clientId) => {

    let add = `http://localhost:58546/api/payment/${clientId}`;
    console.log(add);
    const resp = await axios.get(add);
    let data = resp.data;

    myGetData(data);
}
function Payment(props) {
    let location = useLocation();
    let tempClient = location.state.client;
    console.log(tempClient, "tmp");
    const [client, setClient] = useState({ ...tempClient });
    console.log(client, "client");
    const [payment, setPayment] = useState({});
    const onGetData = (paymet) => {
        console.log(paymet);
        setPayment(paymet);
    }

    return (
        <div className="App">
            <div>
                <GetPayment onClickClients={onGetData} clientId={client.ID} />
            </div>

            <PaymentForm getData={onGetData} client={client} payment={payment} />

        </div>
    );
}
export default Payment;
