import React, { useState, useEffect } from "react"
import "./Payment.css"
import "./Movies.css"
import axios from "axios"

import {
  BrowserRouter as Router,
  useHistory,
  useLocation,
  Switch,
  Route,
  Link
} from "react-router-dom"
const apiLocation = "http://localhost:6600/"
function ShowMovie() {}
function SearchForm(props) {
  const [movieName, setMovieName] = useState("")
  //  const location = useLocation();
  function handleChange(evt) {
    const value = evt.target.value
    setMovieName(value)
  }
  async function onSubmitForm(event) {
    let jsonSent = { movieName: movieName }

    event.preventDefault()
    const resp = axios.post(apiLocation + "api/Movies", jsonSent)
    var movie = (await resp).data
    props.SetMovie(movie)
  }
  return (
    <div>
      <h2>Search Movies</h2>

      <form onSubmit={onSubmitForm}>
        <div>
          <label> Movie name:</label>
          <input
            type="text"
            name="movieName"
            value={movieName}
            onChange={handleChange}
          ></input>
        </div>
        <div>
          <input type="submit"></input>
        </div>
      </form>
    </div>
  )
}

function DisplayMovie(props) {
  return (
    <div class="clearDiv">
      <div>hi</div>
      <div class="movName">{props.move.Title}</div>
      <div class="actors">{props.move.Actors}</div>
      <img class="poster" src={props.move.Poster}></img>
      <span class="plot">{props.move.Plot}</span>
    </div>
  )
}
function AddToDb(props) {
  return (
    <div>
      <button onClick={props.SaveMovie}>Save</button>
    </div>
  )
}
function AllMovies() {
  return <div></div>
}
function Movies(props) {
  const [movie, setMovie] = useState({})
  function handleSetMovie(mov) {
    console.log(mov, "handleset1")

    console.log({ ...mov }, "handleset3")
    let mov1 = { Poster: "x", Actors: "y" }
    console.log(mov1, "handleset5")

    setMovie({ mov1, Title: "hi" })

    console.log(movie, "handleset2")
  }
  async function handleSave() {
    console.log("save")
    await axios.post(apiLocation + "api/Movies/add", movie)
  }
  return (
    <div className="App">
      <SearchForm SetMovie={handleSetMovie} />
      <DisplayMovie move={movie} />
      <AddToDb SaveMovie={handleSave} />
      <AllMovies />
    </div>
  )
}
export default Movies
