import React, { useState, useEffect } from "react"
import "./cardgame.css"
import axios from "axios"
const cards = [
  {
    img: "http://localhost:3000/img/vue.svg",
    name: "vue"
  },
  {
    img: "http://localhost:3000/img/angular.svg",
    name: "angular"
  }
]
function Card(props) {
  return (
    <div>
      hi
      <img class="frontFace" src={require(props.img)}></img>
    </div>
  )
}
function Cards() {
  return (
    <div>
      {cards.map((card) => (
        <Card {...card} />
      ))}
    </div>
  )
}
export default Cards

