import React, { useState, useEffect } from 'react';
import './Payment.css';
import './Movies.css';
import axios from 'axios';

import {
    BrowserRouter as Router,
    useHistory,
    useLocation,
    Switch,
    Route,
    Link
} from "react-router-dom";
const Hist = (props) => {
    const history = useHistory();
    function handleHistory(event) {
        event.preventDefault();
        console.log("Payment");
        history.push({
            pathname: '/home'
            //  state: { client: props.client }
        });
        //  history.push("/home");
    }

    return (
        <button onClick={handleHistory}>Back to Players</button>
    );

}
function SearchForm(props) {
    const [movieName, setMovieName] = useState("");
    //  const location = useLocation();
    function handleChange(evt) {
        const value = evt.target.value;
        setMovieName(value);
    }
    async function onSubmitForm(event) {
        let jsonSent = { movieName: movieName };
        console.log(jsonSent, "jsonSent ");
        event.preventDefault();
        const resp = axios.post('http://localhost:58546/api/Movies', jsonSent);

    }
    return (
        <div>
            <h2>Search Movies</h2>

            <form onSubmit={onSubmitForm}>
                <div>
                    <label> Movie name:</label>
                    <input type="text" name="movieName" value={movieName} onChange={handleChange} ></input>
                </div>
                <div>
                    <input type="submit"></input>
                </div>
            </form>
        </div>
    );
}

function Movies(props) {

    return (
        <div className="App">
            <div>
                <Hist />
            </div>
            <SearchForm client={client} />
        </div>
    );
}
export default Movies;
