import React, {useState} from 'react';
import axios from 'axios';
import logo from './logo.svg';
import './App.css';

const testData = [{
  ID:"324",
    Name: "gilad",
  },
  {
    ID:"323444",
      Name: "or",
    }]
  const TableHeader = ()=>{
    return (
    <tr>
      <td> ID</td>
      <td> Name</td>
    </tr>)
  }

  const OneCient = (props)=>{
    return (
    <tr>
       <td><input  type="text" placeholder={ props.ID}/></td>
      <td> <input  type="text" placeholder={ props.Name}/></td>
    </tr>)
  }
  const TableValues = (props)=>{
   return(props.clients.map(client =><OneCient {...client}/>));
  }
  const AddClient = (props)=>{
   return( <button 
             onClick={() => props.addClient()}
             >Add</button>)

  }
  const getData =async (onGetData)=>{
    const resp = await axios.get(`http://localhost:58546/api/clients`);
    console.log(resp.data);
    //onGetData(resp);

  }
  function App() {  
    const [readClients, setReadClients] = useState([]);
    const onAddClient = ()=> {setReadClients(readClients.concat({}))}
    const onGetData = (clientsArray)=>{
      console.log(clientsArray);
      setReadClients(clientsArray);
    }
    getData(onGetData);
    return (
      <div className="App">
        <header className="App-header">

          <AddClient addClient={onAddClient}/>
          <table  border="1">
            <TableHeader/>
            <TableValues clients = {readClients}/>
          </table>
        </header>
      </div>
    );
  }

export default App;
